package com.class03;

public class DoubleComparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a = 1.2;
		double b = 2.4;

		if (a > b) {
			System.out.println("Double value of " + a + " larger than " + b);
		} else {
			System.out.println("Double value of " + b + " larger than " + a);

		}

	}

}
