package com.class03;

public class PossNeg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a= -11;
		
		if (a>0) {
			System.out.println("Number is positive");
		}
		else if (a<0) {
			System.out.println("Number is negative");
		}
		else {
			System.out.println("Number is 0");
		}

	}

}
