package com.class07;

public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 50;
		int num2 = 1;
		while (num > 0) {
			System.out.println(num);
			num--;
		}
		while (num2 < 21) {
			if (num2 % 2 != 0) {
				System.out.println(num2);
			}
			num2++;
		}

	}

}
